import HeadInfo from "../../components/tools/HeadInfo";
import { getExamRecordList } from "../../api/exam";
export default {
  // 考试记录列表，记录考生参加过地所有考试和考试成绩
  name: 'ExamRecordList',
  components: {
    HeadInfo: HeadInfo
  },
  data: function data() {
    return {
      data: {}
    };
  },
  methods: {
    /**
     * 根据考试记录的id拿到本次考试的详情并查看
     * @param record 考试详情的记录
     */
    viewExamRecordDetail: function viewExamRecordDetail(record) {
      // 直接跳到参加考试的页面，查看所有题目的详细情况
      var routeUrl = this.$router.resolve({
        path: "/exam/record/".concat(record.examId, "/").concat(record.examRecordId)
      });
      // 和点击考试卡片效果一样，跳转到考试页面，里面有所有题目的情况，相当于就是详情了
      window.open(routeUrl.href, '_blank');
    }
  },
  mounted: function mounted() {
    var _this = this;
    // 从后端数据获取考试列表，适配前端卡片
    getExamRecordList().then(function (res) {
      if (res.code === 0) {
        _this.data = res.data;
      } else {
        _this.$notification.error({
          message: '获取考试记录失败',
          description: res.msg
        });
      }
    }).catch(function (err) {
      // 失败就弹出警告消息
      _this.$notification.error({
        message: '获取考试记录失败',
        description: err.message
      });
    });
  }
};
